def test_send_then_get():
    username = accounts[0][0]
    password = accounts[0][1]
    test_server = zmail.server(username, password)
    test_server.send_mail(username, mail_as_dict)
    now_latest_num = test_server.stat()[0]
    t = 0
    while True:
        if now_latest_num < test_server.stat()[0]:
            mail_receive = test_server.get_mail(now_latest_num + 1)
            break
        else:
            t += 1
            if t == 20:
                raise Exception(666)
            time.sleep(0.5)
    for k in mail_as_dict:
        if k != 'attachments':
            assert mail_receive[k] == mail_receive[k]
        else:
            assert mail_receive[k] == parsed_attachments
